import React from "react";
import { useState, useInterval } from "react";

const CountDown = ({ targetISOstring }) => {
  const remain = useInterval(
    () => (new Date(targetISOstring) - new Date()) / 1000,
    10,
  );

  return <div>남은 시간 : {remain}</div>;
};

export default CountDown;
