import React from "react";
import styled from "styled-components";
import ModalEffect from "./ModalEffect";

/**
 * Modal View
 */
// SignIn
export const SignInModal = () => {
  return (
    <ModalEffect>
      <Form>
        <h2>로그인</h2>
        <InputSection>
        <InputBox><small>ID</small><p>앙디</p> </InputBox>
          <input placeholder="아이디를 입력해주세요." />
          <br />
          <small>PW</small>
          <input placeholder="비밀번호를 입력해주세요." type={"password"} />
        </InputSection>
        <BtnSection></BtnSection>
      </Form>
    </ModalEffect>
  );
};
// SignUp
export const SignUpModal = () => {
  return (
    <ModalEffect>
      <Form>
        <h2>회원가입</h2>

        <InputSection>
          <InputBox>
            <p>ID</p> <small>아이디를s</small>
          </InputBox>
          <input placeholder="아이디를 입력해주세요." />

          <InputBox>
            <small>PW</small>
            <p>아이디를s</p>
          </InputBox>
          <input placeholder="비밀번호를 입력해주세요." type={"password"} />
        </InputSection>

        <BtnSection>
          <button>확인</button>
          <button>회원가입</button>
        </BtnSection>
      </Form>
    </ModalEffect>
  );
};

/**
 * Modal Style
 */
// Input
const Form = styled.form`
  width: 30rem;
  height: 25rem;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background-color: tan;
  font-size: 1.5rem;
`;
const InputSection = styled.div`
  width: 60%;
  height: 100px;
  background-color: blue;
  font-size: 1.2rem;
  display: flex;
  flex-direction: column;
  p{
    margin: 0px 10px;
  }
`;
const InputBox = styled.div`
  width: 60%;
  display: flex;
  flex-direction: row;
  background-color: red;
`;

// Button
const BtnSection = styled.div`
  width: 60%;
  height: 50px;
  background-color: pink;
  button{
    width: 50px;
  }
`;
