import React from "react";
import styled from "styled-components";
import { auth, db } from "../../utils/firebase";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { collection, addDoc } from "firebase/firestore";

function SignUpPage() {
  const signup = async () => {
    // 밸리데이션
    if (pwRef.current.value ===! confirmPwRef.current.value) {
      return alert("ㄴㄴ");
    }

    const user = await createUserWithEmailAndPassword(
      auth,
      idRef.current.value,
      pwRef.current.value,
    );
    console.log(user);

    const user_doc = await addDoc(collection(db, "users"), {
      user_id: user.user.email,
      name: nameRef.current.value,
    });
    console.log(user_doc.id);
  };

  const idRef = React.useRef(null);
  const nameRef = React.useRef(null);
  const pwRef = React.useRef(null);
  const confirmPwRef = React.useRef(null);

  return (
    <Form>
      <h2>회원가입</h2>
      <InputSection>
        <InputValue>
          <small>Id</small> <p>중복된 아이디가 있습니다.</p>
        </InputValue>
        <input placeholder="아이디를 입력해주세요." ref={idRef} />

        <InputValue>
          <small>Name</small>
        </InputValue>
        <input placeholder="이름을 입력해주세요." ref={nameRef} />

        <InputValue>
          <small>Password</small> <p>6자리 이상 입력해주세요.</p>
        </InputValue>
        <input
          placeholder="비밀번호를 입력해주세요."
          type={"password"}
          ref={pwRef}
          autoComplete="off"
        />

        <InputValue>
          <small>Confirm Password</small>
        </InputValue>
        <input
          placeholder="비밀번호를 다시 입력해주세요."
          type={"password"}
          ref={confirmPwRef}
          autoComplete="off"
        />
      </InputSection>
      <BtnSection>
        <Button onClick={signup} type="button">
          회원가입
        </Button>
      </BtnSection>
    </Form>
  );
}

const Form = styled.form`
  width: 25rem;
  height: 30rem;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  border-radius: 10px;
  justify-content: center;
  background-color: tan;
  font-size: 1.5rem;
  h2 {
    margin-bottom: 5%;
  }
`;
const InputSection = styled.div`
  width: 80%;
  height: 50%;
  font-size: 1.5rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  input {
    width: 80%;
    height: 30px;
    margin-bottom: 5%;
    border-radius: 5px;
    border-style: none;
    padding: 1%;
  }
`;
const InputValue = styled.div`
  width: 80%;
  display: flex;
  flex-direction: row;
  align-items: center;
  font-weight: 800;
  p {
    margin-left: 3%;
    font-size: 0.8rem;
  }
`;

// Button
const BtnSection = styled.div`
  width: 65%;
  height: 50px;
  display: flex;
  flex-direction: row;
  justify-content: center;
`;
const Button = styled.button`
  width: 50%;
  height: 100%;
  border-style: none;
  border-radius: 5px;
  &:first-child {
    margin-right: 3%;
  }
  &:hover {
    cursor: pointer;
  }
`;

export default SignUpPage;
