import { React, useState } from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import useInterval from "../../hooks/useInterval";

const Step1Page = () => {
  const navigate = useNavigate();

  const [clickCounter, setClickCounter] = useState(0);
  const [count, setCount] = useState(10);
  const [isClear, setIsClear] = useState(null);

  // 클릭횟수
  const increase = () => {
    setClickCounter(clickCounter + 1);
  };

  // 시간초
  useInterval(
    () => {
      setCount(count - 1);
    },
    !clickCounter || count === 0 ? null : 1000,
  );

  // // 판정
  // const isNext = (e) => {
  //   setIsClear(true);
  //   if (clickCounter !== 45) {
  //     setIsClear(false);
  //   }
  // };

  return (
    <Wrap>
      <TitleBox>
        <h2> [1단계] 은밀한 초콜릿 녹이기 </h2>
        <p> Tip! 초콜릿 중탕에 좋은 온도는 45도입니다. </p>
      </TitleBox>
      <PlayingBox>
        <Melting>{/* 초콜렛 녹이는 일러스트 */}</Melting>
        <Thermometer>
          {/* 온도계 일러스트 */}

          {/* 10초 타이머 설정 */}
          <p> 남은 시간 : {count} 초</p>

          {/* 마우스 클릭 횟수 측정 */}
          <h3>현재 {clickCounter} 도</h3>
          {count === 0 ? (
            <BtnOff>온도 올리기!</BtnOff>
          ) : (
            <button onClick={increase}> 온도 올리기! </button>
          )}
        </Thermometer>
      </PlayingBox>

      {/* 실패 */}


      {/* 성공  */}
      {count === 0 && clickCounter === 45 ? (
        <Next
          onClick={() => {
            navigate("step2");
          }}
        >
          성공
          <br />
          다음 단계로!
        </Next>
      ) : null}
    </Wrap>
  );
};

const Wrap = styled.div`
  width: 80%;
  height: fit-content;
  margin-top: 80px;
  padding: 20px;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border: 2px red dotted;
`;
const TitleBox = styled.div`
  width: 50%;
  margin: 10px 0;
  h2 {
    font-size: 2.2rem;
  }
`;

const PlayingBox = styled.div`
  width: 90%;
  height: 450px;
  padding: 3%;
  box-sizing: border-box;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
`;

const Melting = styled.div`
  width: 70%;
  height: 100%;
`;

const Thermometer = styled.div`
  width: 30%;
  height: 100%;
`;

const BtnOff = styled.button`
  cursor: not-allowed;
`;

const Next = styled.div`
  width: 100px;
  height: fit-content;
  font-size: 1.2rem;
  font-weight: bold;
  border: 1px solid red;
  cursor: pointer;
`;

export default Step1Page;
