import React from "react";
import styled from "styled-components";
import { TitleBox, PlayingBox } from "./Step1Page";
import useInterval from "../../hooks/useInterval";

const Step2Page = () => {
  const [count, setCount] = React.useState(0);
  const [stickPosition, setStickPostion] = React.useState();
  useInterval(
    () => {
      setCount(count - 1);
    },
    !clickCounter || count === 0 ? null : 1000,
  );

  const reset = (e) => {
    window.location.reload();
  };
  const stickDrop = () => {};

  return (
    <Wrap>
      <TitleBox>
        <h2>[2단계] 깐깐한 막대과자 손님</h2>
      </TitleBox>
      <PlayingBox>
        <Boll />
        <Choco />
        <StickBox>
          <Stick>
            <Marking />
          </Stick>
        </StickBox>
      </PlayingBox>
    </Wrap>
  );
};
const Wrap = styled.div`
  width: 80%;
  height: fit-content;
  padding: 20px;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
  bottom: 0;
  border: 2px red dotted;
`;
const Boll = styled.div`
  width: 250px;
  height: 200px;
  border-radius: 0 0 150px 150px;
  background-color: #eee;
  position: absolute;
  bottom: 20px;
`;
const Choco = styled.div`
  width: 240px;
  height: 160px;
  border-radius: 0 0 150px 150px;
  background-color: chocolate;
  position: absolute;
  bottom: 25px;
`;
const StickBox = styled.div`
  width: 20px;
  height: 150px;
  position: absolute;
  top: 0;
`;
const Stick = styled.div`
  width: 20px;
  height: 150px;
  border-radius: 50px;
  background-color: lightgoldenrodyellow;
  position: relative;
`;
const Marking = styled.div`
  width: 20px;
  height: 2px;
  background-color: red;
  position: absolute;
  top: 40px;
`;

export default Step2Page;
