import React from "react";
import styled from "styled-components";
import ModalEffect from "./ModalEffect";

/**
 * Modal View
 */
// SignIn
export const SignInModal = () => {
  return (
    <ModalEffect>
      <Form>
        <h2>로그인</h2>
        <InputSection>
          <small>ID</small>
          <input placeholder="아이디를 입력해주세요." />
          <br />
          <small>PW</small>
          <input placeholder="비밀번호를 입력해주세요." type={"password"} />
        </InputSection>
        <BtnSection></BtnSection>
      </Form>
    </ModalEffect>
  );
};
// SignUp
export const SignUpModal = () => {
  return (
    <ModalEffect>
      <Form>
        <h2>회원가입</h2>
        <InputSection>
          <div>
            <small>ID</small> <input placeholder="아이디를 입력해주세요." />
          </div>

          <small>PW</small>
          <input placeholder="비밀번호를 입력해주세요." type={"password"} />
        </InputSection>
        <BtnSection></BtnSection>
      </Form>
    </ModalEffect>
  );
};

/**
 * Modal Style
 */
// Input
const Form = styled.form`
  width: 40rem;
  height: 30rem;
  margin: 0 auto;
  text-align: center;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  line-height: 50px;
`;
const InputSection = styled.div`
  width: 80%;
  height: 200px;
  background-color: blue;
  /* display: flex;
  flex-direction: column; */

  input {
    width: 60%;
  }
`;
// Button
const BtnSection = styled.div`
  width: 80%;
`;
