import React from "react";

const CountDown = ({ targetISOstring }) => {
  const calculator = () => Math.floor(new Date(targetISOstring) - new Date());

  const [remain, setRemain] = useState(calculator());
  useInterval(() => {
    setRemain(calculator());
  }, 100);

  return <div>남은 시간 : {remain}</div>;
};

export default CountDown;
