import { React, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import styled from "styled-components";
import useInterval from "../../hooks/useInterval";

const Step1Page = () => {
  const navigate = useNavigate();

  const [clickCounter, setClickCounter] = useState(0);
  const [count, setCount] = useState(10);
  const [isClear, setIsClear] = useState(null);

  // ÌÅ¥Î¶≠ÌöüÏàò
  const increase = () => {
    setClickCounter(clickCounter + 1);
  };

  // ÏãúÍ∞ÑÏ¥à
  useInterval(
    () => {
      setCount(count - 1);
    },
    !clickCounter || count === 0 ? null : 1000,
  );

  const reset = (e) => {
    window.location.reload();
  };

  return (
    <Wrap>
      <TitleBox>
        <h2> [1Îã®Í≥Ñ] ÏùÄÎ∞ÄÌïú Ï¥àÏΩúÎ¶ø ÎÖπÏù¥Í∏∞ </h2>
        <p> Tip! Ï¥àÏΩúÎ¶ø Ï§ëÌÉïÏóê Ï¢ãÏùÄ Ïò®ÎèÑÎäî 45ÎèÑÏûÖÎãàÎã§. </p>
      </TitleBox>
      <PlayingBox>
        <Melting>{/* Ï¥àÏΩúÎ†õ ÎÖπÏù¥Îäî ÏùºÎü¨Ïä§Ìä∏ */}</Melting>
        <Thermometer>
          {/* Ïò®ÎèÑÍ≥Ñ ÏùºÎü¨Ïä§Ìä∏ */}

          {/* 10Ï¥à ÌÉÄÏù¥Î®∏ ÏÑ§Ï†ï */}
          <p> ÎÇ®ÏùÄ ÏãúÍ∞Ñ : {count} Ï¥à</p>

          {/* ÎßàÏö∞Ïä§ ÌÅ¥Î¶≠ ÌöüÏàò Ï∏°Ï†ï */}
          <h3>ÌòÑÏû¨ {clickCounter} ÎèÑ</h3>
          {count === 0 ? (
            <BtnOff>Ïò®ÎèÑ Ïò¨Î¶¨Í∏∞!</BtnOff>
          ) : (
            <button onClick={increase}> Ïò®ÎèÑ Ïò¨Î¶¨Í∏∞! </button>
          )}
        </Thermometer>
      </PlayingBox>

      {/* Ïã§Ìå® */}
      {count === 0 && clickCounter !== 45 ? (
        <ResultBtn onClick={reset}>
          Ïã§Ìå®üò≠ <br />
          Îã§ÏãúÌïòÍ∏∞!
        </ResultBtn>
      ) : null}

      {/* ÏÑ±Í≥µ  */}
      {count === 0 && clickCounter === 45 ? (
        <Link to="/game/step2">
          <ResultBtn>
            ÏÑ±Í≥µüéâ
            <br />
            Îã§Ïùå Îã®Í≥ÑÎ°ú!
          </ResultBtn>
        </Link>
      ) : null}
    </Wrap>
  );
};

export const Wrap = styled.div`
  width: 80%;
  height: fit-content;
  /* margin-top: 50px; */
  padding: 20px;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border: 2px red dotted;
`;
export const TitleBox = styled.div`
  width: 50%;
  margin: 10px 0;
  h2 {
    font-size: 2.2rem;
  }
`;

export const PlayingBox = styled.div`
  width: 90%;
  height: 450px;
  padding: 3%;
  box-sizing: border-box;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
`;

const Melting = styled.div`
  width: 70%;
  height: 100%;
`;

const Thermometer = styled.div`
  width: 30%;
  height: 100%;
`;

const BtnOff = styled.button`
  cursor: not-allowed;
`;

const ResultBtn = styled.div`
  width: 120px;
  height: fit-content;
  font-size: 1.2rem;
  font-weight: bold;
  border-radius: 5px;
  background-color: hotpink;
  cursor: pointer;
`;

export default Step1Page;
